mapper.select:
insert 
  into calendar
       (
         ca_no
       , user_no
       , ca_title
       , ca_memo
       , ca_startdate
       , ca_enddate
       , ca_color
       , ca_category
       )
  values
       (
         SEQ_calendar.nextval
       , #{userNo}
       , #{calTitle}
       , #{calMemo}
       , #{calStartDate}
       , #{calEndDate}
       , #{calColor}
       , #{calCategory}
       )

mapper.update:
update calendar
   set ca_title = #{calTitle}
     , ca_memo = #{calMemo}
     , ca_startdate = #{calStartDate}
     , ca_enddate = #{calEndDate}
     , ca_color = #{calColor}
 where ca_no = #{calNo}
