mapper.insert:
insert
  into PROJECT
  	 (
	   PRO_NO
	 , PRO_TITLE
	 , PRO_GRANT_WHETHER
	 , PRO_MANAGER
	 , PRO_NUMBERPEOPLE
     )
values
     (
       seq_project.nextval
     , #{proTitle}
     , #{proGrantWhether}
     , #{proManager}
     , #{proNumberPeople}
     )

insert 
  into WORKBOARD
  ( 
   	WORKBOARD_NO
  , PRO_NO 
  ,	USER_NO
  ,	WORK_MANAGER
  ,	WORK_STARTDATE
  ,	WORK_ENDDATE
  ,	WORK_STATUS
  ,	WORK_PRIORITY
  ,	WORK_PROGRESS
  ,	WORK_CONTENT
  ,	WORKBOARD_WRITER
  , WORK_TITLE

  )
  VALUES(
    seq_workboard.nextval
  , #{proNo} 
  ,	#{userNo}
  ,	#{workManager}
  ,	#{workStartDate}
  ,	#{workEndDate}
  ,	#{workStatus}
  ,	#{workPriority}
  ,	#{workProgress}
  ,	#{workContent}
  ,	#{workBoardWriter}
  , #{workTitle}
  )

insert into ATTACHMENT ( ATTACH_NO , WORKBOARD_NO , ATTACH_ORIGIN_NAME , ATTACH_CHANGE_NAME , ATTACH_STATUS
) VALUES( SEQ_ATTACHMENT.nextval , seq_workboard.currval , #{atOriginName} , #{atChangeName} , 'Y'
)
