mapper.insert:
insert
  into todo
       (
         todo_no
       , user_no
       , todo_title
       , todo_memo
       , todo_imp
       , todo_date
       , todo_status
       )
 values
       (
         SEQ_TODO.nextval
       , #{userNo}
       , #{todoTitle}
       , #{todoMemo}
       , #{todoImp}
       , #{todoDate}
       , 'S'
       )

mapper.update:
update todo
   set todo_status = #{todoStatus}
 where todo_no = #{todoNo}

update todo
   set todo_title = #{todoTitle}
     , todo_memo = #{todoMemo}
     , todo_imp = #{todoImp}
 where todo_no = #{todoNo}
